import Link from 'next/link';
import { withRouter } from 'next/router';
import Layout from '../../components/Layout';

// Blog Sample
const Content = withRouter((props) => {
    return (
        <>
            <h3>My Blog</h3>

            <Link href="/blog">
                <a className="link">‚Üê Back</a>
            </Link>

            <div className="blog">
                <img src={ props.router.query.image } />
                <div>
                    <p className="title">{ props.router.query.title }</p>
                    <p className="content-text">{ props.router.query.content }</p>
                </div>
            </div>

            <style jsx>{`
                .blog {
                    display: flex;
                    align-items: start;
                }
                .blog img {
                    display: block;
                    width: 200px;
                    height: 200px;
                    object-fit: cover;
                    margin: 0 30px 0 0;
                    background: #eee;
                }
                .title {
                    font-size: 25px;
                    color: #3ea8a3;
                    margin: 0 0 10px 0;
                }
                .content-text {
                    text-align: justify;
                    font-size: 16px;
                    line-height: 1.75em;
                }
                .link {
                    display: block;
                    font-size: 15px;
                    margin: 0 0 20px 0;
                }
            `}</style>
        </>
    );
});
const Blog = (props) => {
    return (
        <Layout>
            <Content />
        </Layout>
    );
}

export default Blog;
