import { useState, useEffect } from 'react';
import axios from 'axios';
import Link from 'next/link';
import Layout from '../../components/Layout';

const URL = 'https://script.google.com/macros/s/AKfycby6E9z0BBaUocaa-6Gl8aF5YBka-WYTNB4_CT1tN8RBICGFo1o/exec';

const Index = () => {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const res = await axios.get(URL);
            setArticles(res.data);
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <Layout>
            <h3>Gas API</h3>
            <ul className="items">
                {articles.map((item) => (
                    <li className="item" key={item.id}>
                        <Link as={`/animation/${item.id}`} href={`/gasapi/detail?id=${item.id}&title=${item.title}`}>
                            <a className="item-link"><img src={item.urlToImage} /></a>
                        </Link>
                        <Link as={`/animation/${item.id}`} href={`/gasapi/detail?id=${item.id}&title=${item.title}`}>
                            <a className="item-link">{item.title}</a>
                        </Link>
                    </li>
                ))}
            </ul>
        </Layout>
    );
}
export default Index;
