// Fetch Sample
const PostLink = ({ articles }) => {
    return (
        <li>
            {articles.map((item) => (
                <Link as={`/p/${item.id}`} href={`/post?title=${item.title}&description=${item.description}`}>
                    <a>{item.title}</a>
                </Link>
            ))}
        </li>
    );
}

const URL = 'https://script.google.com/macros/s/AKfycby6E9z0BBaUocaa-6Gl8aF5YBka-WYTNB4_CT1tN8RBICGFo1o/exec';

const Index = (props) => {
    const [articles, setArticles] = useState([]);  

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const res = await axios.get(URL);
            setArticles(res.data);
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <Layout>
            <h3>Fetch Sample</h3>
            <ul>
                <PostLink />
            </ul>
        </Layout>
    );
}

export default Index;
