import Link from 'next/link';

// Blog Sample
const PostLink = ({ id, title, content, tag, image }) => {
    return (
        <>
        // <li>
        //     <Link as={`/b/${id}`} href={`/blog/detail?title=${title}&content=${content}&tag=${tag}&image=${image}`}>
        //         <a>{title}</a>
        //     </Link>
        // </li>
        <div className="blog">
                <img src={ image } />
                <div className="blog-text">
                    <p className="tag">{ tag }</p>
                    <p className="title">{ title }</p>
                    <p className="content-text">{ content }</p>
                </div>
            </div>

            <style jsx>{`
                .blog {
                    display: flex;
                }
                .blog img {
                    display: block;
                    width: 200px;
                    height: 200px;
                    object-fit: cover;
                    margin: 0 30px 0 0;
                }
                .title {
                    font-size: 25px;
                    color: #3ea8a3;
                    margin: 0 0 10px 0;
                }
                .content-text {
                    text-align: justify;
                    font-size: 16px;
                    line-height: 1.75em;
                }
                .link {
                    display: block;
                    font-size: 15px;
                    margin: 0 0 20px 0;
                }
            `}</style>
            </>
    );
}
const Index = () => {
    return (
        <Layout> 
