import { withRouter } from 'next/router';
import Layout from '../components/Layout';

const Post = withRouter((props) => {
    return (
        <Layout>
            <h3>My Blog</h3>
            <p className="title">{ props.router.query.title }</p>
            <p className="content">{ props.router.query.content }</p>

            <style jsx>{`
                .title {
                    font-size: 40px;
                    color: #3e3ea8;
                    margin: 0 0 10px 0;
                }
                .content {
                    font-size: 16px;
                }
            `}</style>
        </Layout>
    );
});

export default Post;
