// Fetch Sample
const PostLink = ({ id, title, description }) => {
    return (
        <li>
            <Link as={`/p/${id}`} href={`/post?title=${title}&description=${description}`}>
                <a>{title}</a>
            </Link>
        </li>
    );
}

const URL = 'https://script.google.com/macros/s/AKfycby6E9z0BBaUocaa-6Gl8aF5YBka-WYTNB4_CT1tN8RBICGFo1o/exec';

const Index = (props) => {
    const [articles, setArticles] = useState([]);  

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const res = await axios.get(URL);
            setArticles(res.data);
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <Layout>
            <h3>Fetch Sample</h3>
            <ul>
                {articles.map((item) => (
                    <li key={item.id}>
                        {item.title}
                    </li>
                ))}
            </ul>
        </Layout>
    );
}

export default Index;
