            <Link href="/">
                <a className="link">‚Üê Back</a>
            </Link>

            <p className="title">{ props.router.query.title }</p>
            <p className="content">{ props.router.query.content }</p>

            <style jsx>{`
                .title {
                    font-size: 40px;
                    color: #3ea8a3;
                    margin: 0 0 10px 0;
                }
                .content {
                    font-size: 18px;
                }
                .link {
                    display: block;
                    font-size: 18px;
                    margin: 0 0 0 0;
                }
            `}</style>
        </Layout>
    );
});

export default Post;
