import Link from 'next/link';
import { withRouter } from 'next/router';
import Layout from '../../components/Layout';

const Content = withRouter((props) => {
    return (
        <>
            <h3>My Blog</h3>

            <Link href="/gasapi">
                <a className="prev-link">‚Üê Back</a>
            </Link>

            <div className="blog">
                <img src={ props.router.query.urlToImage } />
                <div className="blog-text">                  
                    <p className="title">{ props.router.query.title }</p>
                    <p className="content-text">{ props.router.query.description }</p>
                    <div className="tag">{ props.router.query.author }</div>
                    <div className="createdAt">{ props.router.query.publishedAt.substr(0,10) }</div>
                </div>
            </div>

            <style jsx>{`
                .blog {
                    display: flex;
                    justify-content: space-between;
                    flex-direction: row-reverse;
                }
                .blog .createdAt {
                    font-size: 14px;
                    color: #696969;
                    margin: 5px 0 0 0;
                }
                .blog .tag {
                    font-size: 12px;
                    color: #696969;
                    margin: 5px 0 0 0;
                }
                .blog img {
                    display: block;
                    width: 250px;
                    height: 200px;
                    object-fit: cover;
                    background: #eee;
                }
                .title {
                    font-size: 25px;
                    font-weight: 500;
                    margin: 10px 0;
                }
                .content-text {
                    text-align: justify;
                    font-size: 15px;
                    line-height: 1.75em;
                }
                .prev-link {
                    display: block;
                    font-size: 15px;
                    margin: 0 0 30px 0;
                }
                @media screen and (max-width: 768px) {
                    .blog {
                        display: block;
                    }
                    .blog img {
                        width: 100%;
                        height: 200px;
                        margin: 0 0 20px 0;
                    }
                }
            `}</style>
        </>
    );
});

const Detail = () => {
    return (
        <Layout>
            <Content />
        </Layout>
    );
}

export default Detail;
