import { useState, useEffect } from 'react';
import axios from 'axios';
import Link from 'next/link';
import Layout from '../../components/Layout';

const URL = 'https://script.google.com/macros/s/AKfycby6E9z0BBaUocaa-6Gl8aF5YBka-WYTNB4_CT1tN8RBICGFo1o/exec';

const Index = () => {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const res = await axios.get(URL);
            setArticles(res.data);
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <Layout>
            <h3>Gas API</h3>

            {articles.map((item) => (
            <div className="gasapi" key={item.id}>
                <img src={ item.urlToImage } />
                <div className="blog-text">
                    <span className="createdAt">{ item.publishedAt }</span>
                    <span className="tag">{ item.author }</span>
                    <p className="title">{ item.title }</p>
                    <p className="content-text">{ item.description.substr(0,40) }.........</p>
                    <Link
                        as={`/animation/${item.id}`}
                        href={
                            `/gasapi/detail?
                            id=${item.id}
                            &author=${item.author}
                            &title=${item.title}
                            &description=${item.description}
                            &url=${item.url}
                            &urlToImage=${item.urlToImage}
                            &publishedAt=${item.publishedAt}`
                        }
                    >
                        <a className="next-link">{ item.title }</a>
                    </Link>
                </div>
            </div>
            ))}

            <style jsx>{`
                .gasapi {
                    display: flex;
                    margin: 0 0 50px 0;
                    padding: 0 0 50px 0;
                    border-bottom: 1px dashed #ccc;
                }
                .gasapi:last-child {
                    margin: 0;
                    padding: 0;
                    border-bottom: none;
                }
                .gasapi .createdAt {
                    font-size: 14px;
                    color: #696969;
                    margin: 0 10px 0 0;
                }
                .gasapi .tag {
                    font-size: 12px;
                    color: #fff;
                    padding: 3px 10px 1px;
                    background: #ff69b4;
                    bottom: 1px;
                    position: relative;
                }
                .blog img {
                    display: block;
                    width: 150px;
                    height: 125px;
                    object-fit: cover;
                    margin: 0 30px 0 0;
                    background: #eee;
                }
                .title {
                    font-size: 20px;
                    font-weight: 500;
                    margin: 10px 0;
                }
                .content-text {
                    text-align: justify;
                    font-size: 15px;
                    line-height: 1.75em;
                }
                .next-link {
                    display: block;
                    font-size: 13px;
                    margin: 10px 0 0 0;
                }
                @media screen and (max-width: 768px) {
                    .gasapi {
                        display: block;
                        margin: 0 0 25px 0;
                        padding: 0 0 25px 0;
                    }
                    .gasapi img {
                        width: 100%;
                        height: 200px;
                        margin: 0 0 20px 0;
                    }
                }
            `}</style>
        </Layout>
    );
}
export default Index;
