const Post = (props) => (
    <Layout>
        <h3>{props.show.name}</h3>

        <Link href="/">
            <a className="link">← Back</a>
        </Link>

        <p className="content">{props.show.summary.replace(/<[/]?p>/g, '')}</p>
        <img src={props.show.image.medium}/>
        <style jsx>{`
            .title {
                font-size: 40px;
                color: #3ea8a3;
                margin: 0 0 10px 0;
            }
            .content {
                text-align: justify;
                font-size: 14px;
                line-height: 1.75em;
            }
            .link {
                display: block;
                font-size: 15px;
            }
        `}</style>
    </Layout>
)

Post.getInitialProps = async (context) => {
  const { id } = context.query;
  const res = await fetch(`https://api.tvmaze.com/shows/${id}`);
  const show = await res.json();

  return { show }
}

export default Post;
