import Layout from '../components/Layout';
import Link from 'next/link';

const PostLink = ({ id, title, content }) => {
    return (
        <li>
            <Link href={`/post?id=${id}&title=${title}&content=${content}`}>
                <a>{title}</a>
            </Link>
        </li>
    );
}

const Index = () => {
    return (
        <Layout>
            <h3>My Blog</h3>
            <ul>
                <PostLink
                    id="1"
                    title="初めてのNextアプリ"
                    content="NextでBlogチュートリアルに挑戦"
                /> 
            </ul>
        </Layout>
    );
}

export default Index;
