// Fetch Sample
const Index = (props) => {
    return (
        <Layout>
            <h3>Fetch Sample</h3>
            <ul>
                {props.shows.map(({show}) => (
                    <li key={show.author}>
                    <Link as={`/p/${show.author}`} href={`/post?id=${show.author}`}>
                        <a>{show.name}</a>
                    </Link>
                    </li>
                ))}
            </ul>
        </Layout>
    );
}
Index.getInitialProps = async () => {
    // https://newsapi.org/v2/top-headlines?country=jp&apiKey=bcf2d5b2225f483dae9b54081c941967
    // const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
    const res = await fetch('https://newsapi.org/v2/top-headlines?country=jp&apiKey=bcf2d5b2225f483dae9b54081c941967');
    const data = await res.json();
    return {
        shows: data,
    }
}

export default Index;
