import Layout from '../../components/Layout';
import Link from 'next/link';

// Blog Sample
const PostLink = ({ id, title, content, tag, image, createdAt }) => {
    return (
        <>
            <div className="blog">
                <img src={ image } />
                <div className="blog-text">
                    <span className="createdAt">{ createdAt }</span>
                    <span className="tag">{ tag }</span>
                    <p className="title">{ title }</p>
                    <p className="content-text">{ content.substr(0,50) }・・・</p>
                </div>
            </div>
            <li>
                <Link as={`/b/${id}`} href={`/blog/detail?title=${title}&content=${content}&tag=${tag}&image=${image}`}>
                    <a className="link">#{title}</a>
                </Link>
            </li>

            <style jsx>{`
                .blog {
                    display: flex;
                }
                .blog .createdAt {
                    font-size: 14px;
                    color: #696969;
                    margin: 0 10px 0 0;
                }
                .blog .tag {
                    font-size: 12px;
                    color: #fff;
                    padding: 3px 10px 1px;
                    background: #ff69b4;
                    bottom: 1px;
                    position: relative;
                }
                .blog img {
                    display: block;
                    width: 100px;
                    height: 100px;
                    object-fit: cover;
                    margin: 0 30px 0 0;
                }
                .title {
                    font-size: 20px;
                    font-weight: 500;
                    margin: 10px 0;
                }
                .content-text {
                    text-align: justify;
                    font-size: 16px;
                    line-height: 1.75em;
                }
                .link {
                    display: block;
                    font-size: 13px;
                    margin: 20px 0 0 0;
                }
            `}</style>
        </>
    );
}
const Index = () => {
    return (
        <Layout> 
            <h3>Blog Sample</h3>
            <ul>
                <PostLink
                    id="1"
                    title="初めての【Next】アプリです"
                    content="NextでBlogチュートリアルに挑戦NextでBlogチュートリアルに挑戦NextでBlogチュートリアルに挑戦NextでBlogチュートリアルに挑戦NextでBlogチュートリアルに挑戦"
                    tag="初心者向け"
                    image="...path..."
                    createdAt="20.06.11"
                />
            </ul>

            <style jsx>{`

            `}</style>
        </Layout>
    );
}

export default Index;
